dw:transform-message.dw:set-property:
%dw 1.0
%output application/xml
---
{
	wsMessageHeader @(xmlns:"http://www.oorsprong.org/websamples.countryinfo"): 
	{
		trackingMessageHeader: {
			applicationId: inboundProperties.applicationid,
			applicationUserId: inboundProperties.applicationuserid when inboundProperties.applicationuserid?
			otherwise inboundProperties.applicationid,
			consumerId: inboundProperties.consumerid when inboundProperties.consumerid? otherwise "test",
			messageId: inboundProperties.enterprisemessageid,
			conversationId: inboundProperties.conversationid when inboundProperties.conversationid?	
			otherwise inboundProperties.applicationid,
			timeToLive: inboundProperties.timetolive when inboundProperties.timetolive? otherwise 45,
			messageDateTimeStamp: inboundProperties.messagedatetimestamp when inboundProperties.messagedatetimestamp?
			otherwise now
		}
	}
}
